import React, { useEffect, useState } from 'react'
import Avatar from '../../assets/User.png'
import Input from '../../components/Input'

const Dashboard = () => {
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('user:details')));
    const [conversations, setConversations] = useState([]);
    const [messages, setMessages] = useState({});
    const [message, setMessage] = useState('');
    const [users, setUsers] = useState([]);
    console.log('user :>>', user);
    console.log(conversations);
    console.log(messages);
    console.log(users);

useEffect(()=>{
    const loggedInUser = JSON.parse(localStorage.getItem('user:details')
)

    const fetchConversations = async()=>{
        const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
            method: 'GET',
            headers:{
                'Content-Type': 'application/json'
            }
        });
        const resData = await res.json();
        setConversations(resData);
    }
    fetchConversations()

}, [])

useEffect(()=>{
    const fetchUsers = async()=>{
        const res = awitfetch(`http://localhost:8000/api/users`,{
            method:'GET',
            headers:{
                'Content-Type' : 'application/json',
            }
        });
        const resData = await res.json();
        setUsers(resData);
    }
    fetchUsers();
},[users])


const fetchMessages = async(conversationId, user)=>{
    const res = await fetch(`http://localhost:8000/api/message/${conversationId}`,{
    method:'GET',
    headers:{
        'Content-Type': 'application/json',
    }
    });
const resData = await res.json()
console.log(resData);
setMessages({messages: resData, receiver: user, conversationId});
}

const sendMessage = async(e)=>{
