import React from 'react'
import Button from '../../components/Button';
import Input from '../../components/Input';

const Form = () => {
  return (
    <div className="bg-white w-[600px] h-[800px] shadow-lg rounded-lg flex flex-col justify-center items-center">
    <div className="text-4xl font-extrabold">WELCOME </div>
    <div className="text-2xl font-light mb-14">SIGN UP NOW</div>
    <Input label="Full Name" name="name" placeholder="Enter your name" className='mb-6'/>
    <Input label="Email" name="email" placeholder="Enter your email" className='mb-6'/>
    <Input label="Mobile Number" name="mobile" placeholder="Enter your Mobile Number" className='mb-6'/>
    <Input label="Password" name="password" placeholder="Enter your Password" className='mb-10'/>
    <Button label="Sign Up" className="w-1/2" />
    <div></div>
    </div>
  )
}

export default Form;
