import React, { useState } from 'react'
import Button from '../../components/Button';
import Input from '../../components/Input';

const Form = ({
  isSignInPage=false,
}) => {
  const [data, setData] = useState({
    ...Button(!isSignInPage && {
      name:'',
      email
    })
  })
  return (
    <div className="bg-white w-[600px] h-[800px] shadow-lg rounded-lg flex flex-col justify-center items-center">
    <div className="text-3xl font-bold mb-2">WELCOME {isSignInPage && "BACK"}</div>
    <div className="text-2xl font-light mb-8">{isSignInPage && "LOG IN"}{!isSignInPage && "SIGN UP"} NOW</div>
   {!isSignInPage && <Input label="Full Name" name="name" placeholder="Enter your name" className='mb-6'/> }
    {!isSignInPage && <Input label="Email" name="email" placeholder="Enter your email" className='mb-6'/>}
    <Input label="Mobile Number" name="mobile" placeholder="Enter your Mobile Number" className='mb-6'/>
    <Input label="Password" name="password" placeholder="Enter your Password" className='mb-8'/>
    <Button label={isSignInPage ? "Log In": "Sign Up"} className="mb-5" />
    <div>{isSignInPage ? "Create an Account ": "Already have an account? "}<span className='text-primary cursor-pointer underline'>{isSignInPage? "Sign Up":"Sign In"}</span></div>
    </div>
  )
}

export default Form;
