import './App.css';
import Dashboard from './modules/Dashboard';
import Form from './modules/form';
import { Routes, Route, Navigate } from 'react-router-dom';

const ProtectedRoute =({children}) =>{
  const isLoggedIn = localStorage.getItem('user:token') !== null
if(!isLoggedIn){
  return <Navigate to={'/users/sign_in'}/>
}else if(isLoggedIn && ['/sign_in', '/sign_up'].includes(Window.location.href)){
  return <Navigate to={'/'}/>
}
return children
}

function App() {
  return (
    <Routes>
      <Route path='/' element={
        <ProtectedRoute>
          <Dashboard/>
        </ProtectedRoute>
      } />
      <Route path='/users/sign_in' element={<Form isSignInPage = {true}/>} />
      <Route path='/users/sign_up' element={<Form isSignInPage={false}/>} />
    </Routes>
    // <div className="bg-[#edf3fc] h-screen flex justify-center items-center">
    //   {/* <Form /> */}
    //   <Dashboard />
    // </div>
  );
}

export default App;
